mapper.select:
SELECT

FROM t_exam_paper_answer

    and create_user = #{createUser}

     and subject_id = #{subjectId}


    and paper_name LIKE CONCAT('%',#{name},'%')

		SELECT count(id) from t_exam_paper_answer
        WHERE exam_paper_id = #{paper_id,jdbcType=INTEGER}
        AND create_user = #{student_id,jdbcType=INTEGER}
        AND status = #{status,jdbcType=INTEGER}

SELECT create_time as name,COUNT(create_time) as value from
        (
		  SELECT DATE_FORMAT(create_time,'%Y-%m-%d') as create_time from t_exam_paper_answer
			WHERE  create_time  between  #{startTime}  and  #{endTime}
		) a
GROUP BY create_time

SELECT

FROM t_exam_paper_answer


    and subject_id = #{subjectId}


    and paper_name LIKE CONCAT('%',#{paperName},'%')


    and create_username = #{username}
